"use strict";(self.webpackChunkcntug=self.webpackChunkcntug||[]).push([[301],{10:n=>{n.exports=JSON.parse('{"blogPosts":[{"id":"kcd-2024-cfp","metadata":{"permalink":"/blog/kcd-2024-cfp","editUrl":"https://github.com/facebook/docusaurus/tree/main/packages/create-docusaurus/templates/shared/blog/kcd-2024-call-for-proposal.md","source":"@site/blog/kcd-2024-call-for-proposal.md","title":"Kubernetes Community Days Taipei 2024 \u958b\u59cb\u5fb5\u7a3f\u5566\uff01","description":"Call for Proposal \u2014\u2014 KCD 2024","date":"2024-04-03T18:00:00.000Z","tags":[{"label":"kcd","permalink":"/blog/tags/kcd"},{"label":"cfp","permalink":"/blog/tags/cfp"}],"readingTime":1.765,"hasTruncateMarker":false,"authors":[{"name":"Gene Kuo","title":"CNTUG Co-Organizer","url":"https://github.com/ching-kuo","imageURL":"https://github.com/ching-kuo.png","key":"genekuo"}],"frontMatter":{"title":"Kubernetes Community Days Taipei 2024 \u958b\u59cb\u5fb5\u7a3f\u5566\uff01","description":"Call for Proposal \u2014\u2014 KCD 2024","slug":"kcd-2024-cfp","authors":"genekuo","tags":["kcd","cfp"],"hide_table_of_contents":false,"date":"2024-04-03T18:00"},"unlisted":false,"nextItem":{"title":"Cloud Native Taiwan User Group 2023 \u5e74\u5ea6\u56de\u9867","permalink":"/blog/2023-year-end-recap"}},"content":"Kubernetes Community Days Taipei 2024 \u958b\u59cb\u5fb5\u7a3f\u5566\uff01\\nCfP \u9023\u7d50\uff1ahttps://pretalx.coscup.org/coscup-2024/\\n\u8b70\u7a0b\u8ecc\u8acb\u9078\u64c7\uff1a\u300cKubernetes Community Day Taipei\u300d\\n\u5373\u65e5\u8d77\u5230 5/9 \u6b62\uff0c\u53ea\u8981\u4f60\u7684\u4e3b\u984c\u7b26\u5408\u4ee5\u4e0b\u689d\u4ef6\u4e4b\u4e00\uff0c\u6b61\u8fce\u4f86\u6295\u7a3f\uff1a\\n- CNCF \u5c08\u6848 https://landscape.cncf.io/\\n- OpenInfra \u5c08\u6848\\n- \u8207\u57fa\u790e\u8a2d\u65bd\u76f8\u95dc\u7684\u958b\u6e90\u5c08\u6848\uff08\u904b\u7b97\u3001\u7db2\u8def\u3001\u5132\u5b58\u7b49\uff09\\n- \u96f2\u7aef\u539f\u751f\u4f7f\u7528\u6848\u4f8b\\n\\n\u5176\u4e2d\u6709\u4e9b\u689d\u4ef6\u8981\u52d9\u5fc5\u9075\u5b88\uff0c\u8acb\u907f\u514d\u4ee5\u4e0b\u5167\u5bb9\uff1a\\n- \u4f9b\u61c9\u5546\u7279\u5b9a (Vendor specific)\\n- \u92b7\u552e\u6216\u5e02\u5834\u71df\u92b7\u5ba3\u50b3\\n- \u672a\u7d93\u8a31\u53ef\u6216\u53ef\u80fd\u662f\u975e\u958b\u6e90\u6280\u8853\\n\\n\u70ba\u4e86\u9f13\u52f5\u65b0\u4eba\u5206\u4eab\uff0cKCD Taipei 2024 \u6709\u53c3\u8207\u9019\u6b21 COSCUP \u7684 First Time Program\uff0c\u6295\u7a3f\u6642\u53ea\u8981\u6309\u4e0b\u300c\u6211\u662f\u7b2c\u4e00\u6b21\u6295\u7a3f COSCUP\uff0c\u9858\u610f\u6536\u5230\u4fee\u6539\u5efa\u8b70\u3002\u300d\uff0c4/30 \u4ee5\u524d\u6703\u5f97\u5230\u4fee\u6539\u5efa\u8b70\uff0c\u63d0\u5347\u5165\u9078\u6a5f\u7387\u5594\uff01\u8d95\u5feb\u63d0\u4ea4\u4f60\u7684\u8b70\u7a0b\u5427\uff01\\n\\nKubernetes Community Days Taipei 2024 is now calling for proposals!\\nCfP link: https://pretalx.coscup.org/coscup-2024/\\nSelect the track \\"Kubernetes Community Day Taipei.\\"\\nFrom now until May 9th, we welcome submissions if your topic meets any of the following criteria:\\n\\n- CNCF projects https://landscape.cncf.io/\\n- OpenInfra projects\\n- Open-source projects related to infrastructure (compute, network, storage, etc.)\\n- Cloud-native use cases\\n\\nSome conditions must be adhered to; please avoid the following content:\\n- Vendor-specific\\n- Sales or marketing promotions\\n- Unauthorized or potentially non-open-source technology\\n\\nKCD Taipei 2024 is participating in the First Time Program for this COSCUP to encourage newcomers to share. When submitting, select \\"Yes, both are true.\\" in response to the question, \\"Is this your first time submitting to COSCUP, and are you willing to receive initial review feedback?\\" You will receive suggestions for improvement before April 30th to increase your chances of being selected! Hurry up and submit your proposal!"},{"id":"2023-year-end-recap","metadata":{"permalink":"/blog/2023-year-end-recap","editUrl":"https://github.com/facebook/docusaurus/tree/main/packages/create-docusaurus/templates/shared/blog/2023_year_end_recap.md","source":"@site/blog/2023_year_end_recap.md","title":"Cloud Native Taiwan User Group 2023 \u5e74\u5ea6\u56de\u9867","description":"Cloud Native Taiwan User Group 2023 Year End Recap","date":"2023-12-25T18:00:00.000Z","tags":[{"label":"year-end-recap","permalink":"/blog/tags/year-end-recap"}],"readingTime":3.72,"hasTruncateMarker":false,"authors":[{"name":"Gene Kuo","title":"CNTUG Co-Organizer","url":"https://github.com/ching-kuo","imageURL":"https://github.com/ching-kuo.png","key":"genekuo"}],"frontMatter":{"title":"Cloud Native Taiwan User Group 2023 \u5e74\u5ea6\u56de\u9867","description":"Cloud Native Taiwan User Group 2023 Year End Recap","slug":"2023-year-end-recap","authors":"genekuo","tags":["year-end-recap"],"hide_table_of_contents":false,"date":"2023-12-25T18:00"},"unlisted":false,"prevItem":{"title":"Kubernetes Community Days Taipei 2024 \u958b\u59cb\u5fb5\u7a3f\u5566\uff01","permalink":"/blog/kcd-2024-cfp"},"nextItem":{"title":"\u91cd\u78c5\u6d88\u606f: CNCF glossary \u5b98\u65b9\u7db2\u7ad9\uff0c\u7e41\u9ad4\u4e2d\u6587\u9996\u6b21\u4e0a\u7dda!\u706b\u71b1\u767b\u5834!\ud83d\udce3\ud83d\udce3\ud83d\udce3","permalink":"/blog/glossary-zh-tw-first-published-2023"}},"content":"## \u5c0e\u8a00\\n\\n\u5728\u98db\u901d\u7684 2023 \u5e74\u88e1\uff0cCloud Native Taiwan User Group\uff08CNTUG\uff09\u4e0d\u50c5\u8209\u8fa6\u4e86\u591a\u5834\u6df1\u5177\u555f\u767c\u6027\u7684 MeetUp\uff0c\u9084\u63a8\u52d5\u4e86\u773e\u591a\u5225\u958b\u751f\u9762\u7684\u5c08\u6848\u3002\u672c\u7bc7\u5831\u544a\u5c07\u70ba\u60a8\u7cbe\u5f69\u5448\u73fe CNTUG \u4e00\u5e74\u4f86\u7684\u8c50\u78a9\u6210\u679c\u3002\\n\\n## \u7cbe\u5f69\u56de\u9867\uff1aMeetup \u6d3b\u52d5\\n\\n\u4eca\u5e74\uff0cCNTUG \u7cbe\u5fc3\u7b56\u5283\u4e26\u6210\u529f\u8209\u8fa6\u4e86 6 \u5834 Meetup\uff0c\u5176\u4e2d\u5305\u542b\u7dda\u4e0a\u53ca\u7dda\u4e0b\u6d3b\u52d5\uff0c\u5145\u5206\u4fc3\u9032\u4e86\u4ea4\u6d41\u8207\u5408\u4f5c\u3002\u7279\u5225\u503c\u5f97\u4e00\u63d0\u7684\u662f\uff0c\u6211\u5011\u9080\u8acb\u4e86\u773e\u591a\u570b\u969b\u7d1a\u8b1b\u8005\uff0c\u70ba\u53f0\u7063\u7684\u6280\u8853\u611b\u597d\u8005\u5e36\u4f86\u4e86\u6700\u524d\u6cbf\u7684\u96f2\u7aef\u539f\u751f\u6280\u8853\u6d1e\u5bdf\u3002\\n\\n\u66f4\u591a\u7cbe\u5f69 Meetup \u5167\u5bb9\uff0c\u6b61\u8fce\u8a2a\u554f [CNTUG CNCF Chapter](https://community.cncf.io/cloud-native-taiwan-user-group/)\u3002\\n\\n## \u76db\u6703\u56de\u9867\uff1aKubernetes Community Day Taiwan\\n\\n![KCD 2023 Talk](/img/KCD_2023.jpg)\\n\\n\u651c\u624b COSCUP\uff0c\u6211\u5011\u6210\u529f\u8209\u8fa6\u4e86\u4e00\u5e74\u4e00\u5ea6\u7684 Kubernetes Community Day Taiwan\u3002\u4eca\u5e74\u7684\u6d3b\u52d5\u66f4\u52a0\u7279\u5225\uff0c\u773e\u591a\u570b\u969b\u8b1b\u8005\u89aa\u81e8\u53f0\u7063\uff0c\u70ba\u672c\u5730\u96f2\u7aef\u539f\u751f\u793e\u7fa4\u5e36\u4f86\u4e86\u96e3\u5f97\u7684\u570b\u969b\u8996\u91ce\u548c\u4ea4\u6d41\u6a5f\u6703\u3002\\n\\n\u8a73\u60c5\u8acb\u53c3\u95b1 [KCD Taiwan 2023 \u6703\u5f8c\u5831\u544a](/pdf/kcd_taiwan_2023.pdf)\u3002\\n\\n## \u5c08\u6848\u4ecb\u7d39\uff1aCNCF Glossary \u4e2d\u6587\u5316\\n\\n\u96f2\u7aef\u539f\u751f Glossary\uff0c\u7531 CNCF \u884c\u92b7\u59d4\u54e1\u6703\u4e3b\u5c0e\uff0c\u63d0\u4f9b\u4e00\u500b\u4f9b\u61c9\u5546\u4e2d\u7acb\u7684\u5e73\u53f0\uff0c\u7d71\u4e00\u96f2\u7aef\u539f\u751f\u6280\u8853\u7684\u5c08\u696d\u8853\u8a9e\u3002\u8a72\u8a5e\u5f59\u8868\u7684\u4e2d\u6587\u672c\u5730\u5316\u5de5\u4f5c\uff0c\u5f97\u76ca\u65bc\u5085\u8000\u5fb7 (Ader Fu)\u3001 \u9ec3\u79c9\u921e (Phil Huang)\u3001 \u90b1\u5b8f\u744b (HungWei Chiu) \u548c\u6797\u81ea\u5747 (John Lin) \u7b49\u4eba\u7684\u4e0d\u61c8\u52aa\u529b\u3002\\n\\n\u66f4\u591a\u8a73\u60c5\uff0c\u656c\u8acb\u95b1\u8b80[\u96f2\u7aef\u539f\u751f Glossary \u7e41\u9ad4\u4e2d\u6587\u7248\u9996\u767c](https://cloudnative.tw/blog/glossary-zh-tw-first-published-2023)\u3002\\n\\n## Infra Labs\uff1a\u652f\u6301\u958b\u6e90\u793e\u7fa4\\n\\nInfra Labs \u5728\u904e\u53bb\u4e00\u5e74\u4e2d\uff0c\u7e7c\u7e8c\u70ba\u958b\u6e90\u793e\u7fa4\u63d0\u4f9b\u514d\u8cbb\u7684\u96f2\u7aef\u8a08\u7b97\u8cc7\u6e90\u3002\u8a31\u591a\u6280\u8853\u6587\u7ae0\u7684\u767c\u8868\u4ee5\u53ca\u5357\u81fa\u7063\u5b78\u751f\u8cc7\u8a0a\u793e\u7fa4 SCIST \u548c UniCourse \u7684\u5e73\u81fa\u5efa\u8a2d\u90fd\u5f97\u76ca\u65bc\u6b64\u3002\u4eca\u5e74\uff0c\u70ba\u4e86\u66f4\u9ad8\u6548\u5229\u7528\u8cc7\u6e90\uff0c\u6211\u5011\u5b8c\u6210\u4e86\u4e00\u6b21\u91cd\u5927\u7684\u7cfb\u7d71\u9077\u79fb\u548c\u898f\u6a21\u8abf\u6574\u3002\\n\\n\u66f4\u591a\u95dc\u65bc Infra Labs \u7684\u8cc7\u8a0a\uff0c\u8acb\u8a2a\u554f [Infra Labs \u5c08\u6848\u7db2\u7ad9](https://docs.cloudnative.tw)\u3002\\n\\n## iThome \u9435\u4eba\u8cfd\u53c3\u8207\\n\\nCNTUG \u4eca\u5e74\u5f88\u69ae\u5e78\u53d7 2023 iThome \u9435\u4eba\u8cfd\u53d7\u9080\u53c3\u8207\u793e\u7fa4\u984c\u76ee\uff0c\u4ee5\u96f2\u7aef\u539f\u751f (Cloud Native) \u70ba\u51fa\u767c\u5ee3\u9080\u5404\u4f4d\u53c3\u8cfd\u8005\u5c31\u9019\u96f2\u7aef\u539f\u751f\u9818\u57df\u5206\u4eab\u5404\u7a2e\u9ad4\u9a57\u8207\u7d93\u9a57\u3002\\n\\n\u7d66\u6240\u6709\u53c3\u8207\u96f2\u7aef\u539f\u751f\u984c\u76ee\u7684\u53c3\u8cfd\u8005\u9f13\u52f5\u8207\u652f\u6301\uff0c\u6240\u6709\u53c3\u8cfd\u6587\u7ae0\u8acb\u8a2a\u554f [2023 iThome \u9435\u4eba\u8cfd](https://ithelp.ithome.com.tw/2023ironman/signup/list?group=cloud-native)\u3002\\n\\n## KubeCon/Cloud Native CON NA \u53c3\u8207\u7d93\u9a57\\n\\n![KubeCon Badge](https://pbs.twimg.com/media/F-RBNxbbgAAbB6n?format=jpg&name=large)\\n\\n\u4eca\u5e74\uff0c\u793e\u7fa4\u6210\u54e1\u9ec3\u79c9\u921e (Phil Huang) \u548c\u90ed\u9756 (Gene Kuo) \u6709\u5e78\u53c3\u8207 KubeCon/Cloud Native CON\uff0c\u4e26\u5206\u4eab\u4e86\u7d93\u71df CNTUG \u7684\u5fc3\u5f97\u548c\u6311\u6230\u3002\u6b61\u8fce\u5927\u5bb6\u5230 CNCF YouTube \u983b\u9053\u6536\u770b\u7cbe\u5f69\u91cd\u64ad\uff0c[\u8b70\u7a0b\u5f71\u7247\u9023\u7d50](https://youtu.be/z-0-X8SMQfc)\u3002\\n## \u8cc7\u91d1\u72c0\u6cc1\\n\\n\u4ee5\u4e0b PDF \u70ba 2023 CNTUG OCF \u6350\u6b3e\u5e33\u6236\u652f\u51fa\u53ca\u6536\u5165\u72c0\u6cc1\u3002\\n\\n[2023 CNTUG OCF \u5c08\u6236\u8ca1\u52d9\u6536\u652f\u8868](/pdf/2023_financial_statement.pdf)\\n\\n\u4eca\u5e74 Infra Labs \u7531\u793e\u7fa4\u6210\u54e1\u5168\u984d\u51fa\u6b3e\u4e26\u672a\u5229\u7528\u5230\u793e\u7fa4\u8cc7\u91d1\uff0c\u800c\u570b\u5916\u8b1b\u8005\u88dc\u52a9\u7684\u90e8\u5206\u7531 COSCUP \u8d0a\u52a9\uff0c\u552f\u4e00\u652f\u51fa\u70ba cloudnative.tw \u57df\u540d\u8cbb\u7528\u3002\\n\\n\u6b61\u8fce\u900f\u904e [OCF \u500b\u4eba\u6350\u6b3e](https://ocf.neticrm.tw/civicrm/contribute/transact?reset=1&id=29)\u6301\u7e8c\u8d0a\u52a9 CNTUG \u7684\u904b\u4f5c\u3002"},{"id":"/glossary-zh-tw-first-published-2023","metadata":{"permalink":"/blog/glossary-zh-tw-first-published-2023","editUrl":"https://github.com/facebook/docusaurus/tree/main/packages/create-docusaurus/templates/shared/blog/glossary-zh-tw-first-published-2023.md","source":"@site/blog/glossary-zh-tw-first-published-2023.md","title":"\u91cd\u78c5\u6d88\u606f: CNCF glossary \u5b98\u65b9\u7db2\u7ad9\uff0c\u7e41\u9ad4\u4e2d\u6587\u9996\u6b21\u4e0a\u7dda!\u706b\u71b1\u767b\u5834!\ud83d\udce3\ud83d\udce3\ud83d\udce3","description":"CNCF glossary official website, Traditional Chinese is online for the first time! Hot debut!(2023/11/03)","date":"2023-11-07T18:00:00.000Z","tags":[{"label":"CNCF","permalink":"/blog/tags/cncf"},{"label":"glossary","permalink":"/blog/tags/glossary"}],"readingTime":4.115,"hasTruncateMarker":false,"authors":[{"name":"Ader Fu","title":"Cloud Native Taiwan User Group Co-Organizer","url":"https://github.com/ydFu","imageURL":"https://github.com/ydFu.png","key":"ydFu"}],"frontMatter":{"title":"\u91cd\u78c5\u6d88\u606f: CNCF glossary \u5b98\u65b9\u7db2\u7ad9\uff0c\u7e41\u9ad4\u4e2d\u6587\u9996\u6b21\u4e0a\u7dda!\u706b\u71b1\u767b\u5834!\ud83d\udce3\ud83d\udce3\ud83d\udce3","description":"CNCF glossary official website, Traditional Chinese is online for the first time! Hot debut!(2023/11/03)","authors":"ydFu","tags":["CNCF","glossary"],"hide_table_of_contents":false,"date":"2023-11-07T18:00"},"unlisted":false,"prevItem":{"title":"Cloud Native Taiwan User Group 2023 \u5e74\u5ea6\u56de\u9867","permalink":"/blog/2023-year-end-recap"},"nextItem":{"title":"\u8d0a\u52a9\u5546\u52df\u96c6: CNCF Sustainability Week - Taiwan x Green Software Foundation","permalink":"/blog/sponsor-cnsw-2023"}},"content":"![glossary-zh-tw website](/img/cncf-glossary-zh-tw.png)\\n\\n## \u91cd\u78c5\u6d88\u606f: CNCF glossary \u5b98\u65b9\u7db2\u7ad9\uff0c\u7e41\u9ad4\u4e2d\u6587\u9996\u6b21\u4e0a\u7dda!\u706b\u71b1\u767b\u5834!\ud83d\udce3\ud83d\udce3\ud83d\udce3(2023/11/03)\\n\u5927\u5bb6\u53ef\u4ee5\u76f4\u63a5\u9023\u4e0a CNCF glossary: https://glossary.cncf.io/zh-tw/\\n\uff0c\u91dd\u5c0d\u96f2\u7aef\u539f\u751f\u8a5e\u5f59\u9032\u884c\u67e5\u95b1\uff0c\u76ee\u524d\u9996\u6b21\u4e0a\u7dda\u7e3d\u5171\u6709 44 \u500b CNCF \u7e41\u9ad4\u4e2d\u6587\u5167\u5bb9\uff0c\u5305\u542b Kubernetes, DevOps \u7b49\u96f2\u7aef\u539f\u751f\u5167\u5bb9\u3002\\n\\n## \u5c08\u6848\u4ecb\u7d39\\n\u96f2\u7aef\u539f\u751f Glossary \u7531 CNCF \u884c\u92b7\u59d4\u54e1\u6703 (CNCF Marketing Committee-\u5546\u696d\u50f9\u503c\u5c0f\u7d44\u59d4\u54e1\u6703\uff09\u767c\u8d77\uff0c\\nGlossary \u63d0\u4f9b\u6240\u6709\u53c3\u8207\u8005\u4f7f\u7528\u4e14\u4f9b\u61c9\u5546\u4fdd\u6301\u4e2d\u7acb\u7684\u5e73\u53f0\uff0c\u4ee5\u7d44\u7e54\u96f2\u7aef\u539f\u751f\u6280\u8853\u7684\u5171\u4eab\u8a5e\u5f59\u3002\\n\u96f2\u7aef\u539f\u751f Glossary \u7684\u4e2d\u6587\u672c\u5730\u5316\u5de5\u4f5c\u7531[\u5085\u8000\u5fb7(Ader Fu)](https://github.com/ydFu)\u3001\\n[\u9ec3\u79c9\u921e(Phil Huang)](https://github.com/pichuang)\u3001\\n[\u90b1\u5b8f\u744b(HungWei Chiu)](https://github.com/hwchiu)\\n\u548c[\u6797\u81ea\u5747(John Lin)](https://github.com/johnlinp)\u9032\u884c\u8ca2\u737b\u8207\u7dad\u8b77\u3002\\n\\n\u800c\u672c\u6b21\u4e0a\u7dda\u7684\u6210\u529f\u9700\u8981\u7279\u5225\u611f\u8b1d\u4f86\u81ea\u7e41\u4e2d\u7684\u8ca2\u737b\u8005\u5011\uff0c\u8f9b\u52e4\u52aa\u529b\u70ba\u78ba\u4fdd CNCF glossary \u7e41\u9ad4\u4e2d\u6587\u7684\u672c\u5730\u5316\u5167\u5bb9\uff0c\u4e5f\u662f\u6210\u529f\u4e0a\u7dda\u6700\u5927\u4e00\u584a\u7684\u62fc\u5716\u3002\\n\u7279\u5225\u611f\u8b1d\u7684\u8ca2\u737b\u8005\u540d\u55ae\uff08\u6309 GitHub ID \u6392\u5217\uff09\u5305\u62ec\uff1a\\n- [Cody Chen](https://github.com/cdxvy30)\\n- [Fionn Kuo](https://github.com/Fionn88)\\n- [gamerslouis](https://github.com/gamerslouis)\\n- [Kay Lin](https://github.com/kaiyee0)\\n- [Kehao Chen](https://github.com/kehao-chen)\\n- [Kevin Huang](https://github.com/KevinSpringfield)\\n- [Wei-Hsiang (Matt) Wang](https://github.com/mattwang44)\\n- [roseyangwc](https://github.com/roseyangwc)\\n- [Rosa Wu](https://github.com/rosawu159)\\n- [tico88612](https://github.com/tico88612)\\n- [Blocka](https://github.com/win5923)\\n\\n## \u5c08\u6848\u8a18\u4e8b\\n\\n### 1. \u5718\u968a\u5efa\u7acb(2023/04/26)\\n    \u7531\u5c0f\u98db\u6a5f[\u9ec3\u79c9\u921e(Phil Huang)](https://github.com/pichuang)\u767c\u8d77\u5efa\u7acb\u5718\u968a\u6d3b\u52d5\uff0c\\n    \u4e00\u958b\u59cbTraditional Chinese Localization Team \uff0c\u7531[\u9ec3\u79c9\u921e(Phil Huang)](https://github.com/pichuang)\u548c[\u5085\u8000\u5fb7(Ader Fu)](https://github.com/ydFu)\u64d4\u4efb\u521d\u59cb Approver\uff0c\u5055\u540c [Cody Chen](https://github.com/cdxvy30)\u7b49 3 \u4f4d volunteer \u9032\u884c\u7c4c\u5099\u3002\\n    - \u5efa\u7f6e\u5718\u968a: https://github.com/cncf/glossary/issues/1987\\n\\n### 2. \u5c08\u6848\u555f\u52d5\u8207\u5c08\u6848\u7ba1\u7406(2023/05/04)\\n    \u4f9d\u7167 ISO-639 \u8207 ISO-3166 \u78ba\u5b9a\u8a9e\u7cfb\u4f7f\u7528 `zh-tw`\u5f8c\uff0c\u7531[\u5085\u8000\u5fb7(Ader Fu)](https://github.com/ydFu)\u5efa\u7acb\u7684\u5b8c\u6210\u5c08\u6848\u672c\u5730\u5316\u7684\u521d\u59cb\u8a2d\u5b9a\u548c\u9996\u6b21\u5167\u5bb9\u63d0\u4ea4\uff0c\u4e26\u4e14\u5275\u5efa\u7e41\u9ad4\u4e2d\u6587\u5c08\u7528\u7684 dev-tw branch\uff0c\u5176\u4e2d\u4e5f\u7d93\u7531 [Kay Lin](https://github.com/kaiyee0)\u5efa\u8b70\u4e0b\u555f\u7528 GitHub \u5167\u5efa\u7684 Project \u4f86\u8ffd\u8e64\u672c\u5c08\u6848\u7684\u73fe\u6cc1\u3002\\n    - \u672c\u5730\u5316\u7684\u521d\u59cb\u8a2d\u5b9a\u548c\u9996\u6b21\u5167\u5bb9\u63d0\u4ea4: https://github.com/cncf/glossary/pull/2006\\n    - \u4f7f\u7528 GitHub Project \u7ba1\u7406: https://cloud-native.slack.com/archives/C055KERTNCX/p1691299769462459\\n\\n### 3. \u7e41\u4e2d Approver \u5718\u968a\u589e\u52a0\u65b0\u6210\u54e1(2023/09/01)\\n    \u7531\u65bc\u7576\u524d\u8ca2\u737b\u8005\u5728\u7e41\u9ad4\u4e2d\u6587\u8ca2\u737b\u6709\u8457\u5091\u51fa\u7684\u8868\u73fe\uff0c\\n    [\u90b1\u5b8f\u744b(HungWei Chiu)](https://github.com/hwchiu)\u5728 CNCF \u9818\u57df\u64c1\u6709\u6df1\u539a\u7684\u77e5\u8b58\u548c\u8c50\u5bcc\u7684\u7d93\u9a57\uff0c\\n    [\u6797\u81ea\u5747(John Lin)](https://github.com/johnlinp)\u4e5f\u5728dev-tw \u793e\u7fa4\u4e2d\uff0c\u4e26\u63d0\u4ea4\u8a31\u591a\u8ca2\u737b\u8207\u5be9\u6838\uff0c\\n    \u56e0\u6b64[\u9ec3\u79c9\u921e(Phil Huang)](https://github.com/pichuang)\u63d0\u540d\u4e0a\u8ff0\u5169\u4f4d\u52a0\u5165\u7e41\u9ad4\u4e2d\u6587 approver \u5718\u968a\uff0c\u76ee\u524d\u7e41\u4e2d Approver \u4e3b\u8981\u6210\u54e1\u70ba[\u5085\u8000\u5fb7(Ader Fu)](https://github.com/ydFu)\u3001[\u9ec3\u79c9\u921e(Phil Huang)](https://github.com/pichuang)\u3001[\u90b1\u5b8f\u744b(HungWei Chiu)](https://github.com/hwchiu)\u548c[\u6797\u81ea\u5747(John Lin)](https://github.com/johnlinp)\u3002\\n    - \u65b0\u589e approver :https://github.com/cncf/glossary/pull/2343\\n\\n### 4. \u8ca2\u737b\u7279\u5225\u6d3b\u52d5(2023/10/11)\\n    \u7531\u77fd\u8c37\u725b\u7684\u8015\u7530\u7b46\u8a18\u767c\u8d77\u7684\u8ca2\u737b CNCF Glossary \u53ef\u4ee5\u63db Modern Web Conference (MWC) \u7968\u5377\u7684\u7279\u5225\u6d3b\u52d5\uff0c\u85c9\u6b64\u7d2f\u7a4d\u4e0d\u5c11\u65b0\u9032\u8ca2\u737b\u8005\u3002\\n    - \u7279\u5225\u6d3b\u52d5: https://www.facebook.com/100025250046893/posts/pfbid02VNAGgRaiZYJ3a6Ldjr3SCaGEDuL5F6bnxAaK6TkQp5Ghv5f8zos5ZW5zgHv68p6Rl/?mibextid=WiMSqg\\n\\n### 5. \u7e41\u9ad4\u4e2d\u6587\u5167\u5bb9\u6b63\u5f0f\u4e0a\u7dda(2023/11/03)\\n    \u7531[\u5085\u8000\u5fb7(Ader Fu)](https://github.com/ydFu)\u5b8c\u6210 28 \u500b\u5c0f\u9805\u76ee\u7684\u4e0a\u7dda\u524d\u6e96\u5099\uff0c\u5176\u4e2d\u6e96\u5099\u4e86 46 \u500b\u7e41\u9ad4\u4e2d\u6587\u5167\u5bb9\uff0c\u4e26\u65bc\u7d93\u7531 CNCF maintainer \u5be9\u6838\u5f8c\uff0c\u5167\u5bb9\u6b63\u5f0f\u4e0a\u7dda\u4e26\u4e14\u767c\u5e03\u5728\u5b98\u65b9 CNCF glossary \u7db2\u7ad9\u4e2d\u3002\\n    - \u4e0a\u7dda\u6e96\u5099\u9805\u76ee: https://github.com/cncf/glossary/issues/2355\\n    - \u7e41\u9ad4\u4e2d\u6587\u9996\u6b21\u767c\u5e03: https://github.com/cncf/glossary/pull/2576\\n    - CNCF glossary\u7e41\u9ad4\u4e2d\u6587\u5167\u5bb9: https://glossary.cncf.io/zh-tw/#acknowledgements\\n\\n## \u81f4\u8b1d\\n\u8b1d\u8b1d\u5927\u5bb6\u7684\u52aa\u529b\uff0c\u4e2d\u6587\u672c\u5730\u5316\u7684\u5de5\u4f5c\u4ecd\u5728\u9032\u884c\uff0c\u6b61\u8fce\u820a\u96e8\u65b0\u77e5\u5171\u8944\u76db\u8209!!\u4e00\u8d77\u52aa\u529b!!\ud83d\ude00\\n\\n\u540c\u6b65\u8a0a\u606f:\\n- [CNTUG Facebook - \u516c\u544a\u8cbc\u6587](https://www.facebook.com/groups/cloudnative.tw/permalink/1719077178595610)\\n- [CNCF Slack #glossary-localization-traditional-chinese -\u7f6e\u9802\u6d88\u606f](https://cloud-native.slack.com/archives/C055KERTNCX/p1699264963680389)"},{"id":"sponsor-cnsw-2023","metadata":{"permalink":"/blog/sponsor-cnsw-2023","editUrl":"https://github.com/facebook/docusaurus/tree/main/packages/create-docusaurus/templates/shared/blog/cnsw-2023-call-for-sponsor.md","source":"@site/blog/cnsw-2023-call-for-sponsor.md","title":"\u8d0a\u52a9\u5546\u52df\u96c6: CNCF Sustainability Week - Taiwan x Green Software Foundation","description":"Sponsorship for CNCF Sustainability Week - Taiwan x Green Software Foundation","date":"2023-08-20T18:00:00.000Z","tags":[{"label":"sponsorship","permalink":"/blog/tags/sponsorship"},{"label":"cnsw","permalink":"/blog/tags/cnsw"}],"readingTime":4.195,"hasTruncateMarker":false,"authors":[{"name":"Phil Huang","title":"Cloud Native Taiwan User Group Co-Organizer","url":"https://github.com/pichuang","imageURL":"https://github.com/pichuang.png","key":"pichuang"}],"frontMatter":{"title":"\u8d0a\u52a9\u5546\u52df\u96c6: CNCF Sustainability Week - Taiwan x Green Software Foundation","description":"Sponsorship for CNCF Sustainability Week - Taiwan x Green Software Foundation","slug":"sponsor-cnsw-2023","authors":"pichuang","tags":["sponsorship","cnsw"],"image":"https://tag-env-sustainability.cncf.io/images/tag-environmental-sustainability_color.svg","hide_table_of_contents":false,"date":"2023-08-20T18:00"},"unlisted":false,"prevItem":{"title":"\u91cd\u78c5\u6d88\u606f: CNCF glossary \u5b98\u65b9\u7db2\u7ad9\uff0c\u7e41\u9ad4\u4e2d\u6587\u9996\u6b21\u4e0a\u7dda!\u706b\u71b1\u767b\u5834!\ud83d\udce3\ud83d\udce3\ud83d\udce3","permalink":"/blog/glossary-zh-tw-first-published-2023"}},"content":"![TAG Environmental Sustainability](https://tag-env-sustainability.cncf.io/images/tag-environmental-sustainability_color.svg)\\n\\n\u57fa\u65bc [CNCF Cloud Native Sustainability Week][9] \u5b98\u65b9\u6587\u7ae0\u548c[\u8b70\u7a0b\u6307\u5f15][10]\\n\\n## \u8b70\u7a0b\u7e3d\u89bd\\n\\nCNCF Sustainability Week - Taiwan x Green Software Foundation \u5c07\u65bc\u53f0\u5317 [TBD] \u5408\u8fa6\u6d3b\u52d5\uff0c\u610f\u65e8\u662f\u5e0c\u671b\u80fd\u900f\u904e\u672c\u6d3b\u52d5\u8b93\u5927\u5bb6\u521d\u6b65\u4e86\u89e3 Cloud Native \u8207\u74b0\u5883\u53ef\u6301\u7e8c\u6027\u7684\u95dc\u4fc2\uff0c\u540c\u6642\u6211\u5011\u4e5f\u9080\u8acb\u5230\u4f86\u81ea Green Software Foundation Lodon \u7684\u8b1b\u8005 Sarah Hsu \u4f86\u8ddf\u6211\u5011\u5206\u4eab\u95dc\u65bc\u4ee5 Green Software Foundation \u7684\u89c0\u9ede\u5982\u4f55\u63a8\u52d5\u548c\u843d\u5be6\u7da0\u8272\u8edf\u9ad4\u3002\u672c\u6b21\u6d3b\u52d5\u671f\u671b\u63a1\u7528\u8207\u6703\u773e\u5927\u91cf\u4e92\u52d5\u554f\u7b54\u4ea4\u6d41\u7684\u65b9\u5f0f\uff0c\u6703\u63a1\u7528 Lighting Talk \u8d77\u982d\uff0c\u5f8c\u7e8c\u6703\u6709 Panel Dicussion \u5e0c\u671b\u5927\u5bb6\u591a\u591a\u53c3\u8207\u4e92\u52d5\u3002\\n\\n## \u8b70\u7a0b\u76ee\u7684\\n\\n- \u4e86\u89e3\u7576\u524d\u5f62\u52e2\uff1a\u8207\u793e\u5340\u548c\u884c\u696d\u5408\u4f5c\uff0c\u66f4\u597d\u5730\u4e86\u89e3\u96f2\u7aef\u539f\u751f\u74b0\u5883\u53ef\u6301\u7e8c\u6027\u7684\u5f62\u52e2\u3002\\n- \u8b58\u5225\u77e5\u8b58\u5dee\u8ddd\uff1a\u8b58\u5225\u77e5\u8b58\u5dee\u8ddd\u9818\u57df\u4e26\u9032\u4e00\u6b65\u63a2\u7d22\u96f2\u7aef\u539f\u751f\u74b0\u5883\u53ef\u6301\u7e8c\u6027\u3002\\n- \u4fc3\u9032\u9805\u76ee\u5354\u4f5c\uff1a\u4fc3\u9032\u53c3\u8207\u8005\u4e4b\u9593\u5c31\u8207\u96f2\u7aef\u539f\u751f\u53ef\u6301\u7e8c\u6027\u76f8\u95dc\u7684\u6b63\u5728\u9032\u884c\u7684\u9805\u76ee\u548c\u5de5\u5177\u9032\u884c\u5354\u4f5c\u3002\\n- \u63d0\u9ad8\u610f\u8b58\uff1a\u63d0\u9ad8\u5c0d\u96f2\u7aef\u539f\u751f\u74b0\u5883\u53ef\u6301\u7e8c\u6027\u7684\u8a8d\u8b58\u548c\u4e86\u89e3\u3002\\n- \u63a8\u5ee3 TAG \u74b0\u5883\u53ef\u6301\u7e8c\u6027\u548c CNCF\uff1a\u63a8\u5ee3 #tag-environmental-sustainability \u4e26\u63d0\u9ad8\u4eba\u5011\u5c0d CNCF \u7684\u6574\u9ad4\u8a8d\u8b58\u3002\\n\\n## \u9810\u5b9a\u8b70\u7a0b\u8cc7\u8a0a\\n\\n- \u65e5\u671f: `2023/10/24`\\n- \u6642\u9593: `18:30 - 21:00`\\n- \u5730\u9ede: `\u5be6\u9ad4`\\n- \u6700\u591a\u5ea7\u4f4d\u6578: `\u53d6\u6c7a\u65bc\u5730\u9ede`\\n- \u8a3b\u518a\u7db2\u7ad9: [CNCF Community Group - Cloud Native Taiwan User Group][4]\\n- \u8a9e\u8a00: `\u4e2d\u6587`\\n\\n### \u8b70\u7a0b\u9023\u7d50\\n\\n\u63a1\u7528 Cloud Native Community Groups \u7684\u7cfb\u7d71\\n\\n[CNCF Sustainability Week - Taiwan x Green Software Foundation](https://community.cncf.io/events/details/cncf-cloud-native-taiwan-user-group-presents-cncf-sustainability-week-taiwan-x-green-software-foundation/)\\n\\n## \u5408\u8fa6\u793e\u7fa4\\n\\n- [Green Software Foundation][8]\\n\\n## \u8d0a\u52a9\u65b9\u6848\\n\\n\u55ae\u4f4d: \u65b0\u53f0\u5e63 (NTD)\uff0c\u672a\u7a05\\n\\n- \u7b56\u7565\u8d0a\u52a9\\n    - \u8d0a\u52a9\u91d1\u984d\uff1a50,000 NTD\\n    - \u8d0a\u52a9\u65b9\u6848\u5167\u5bb9\uff1a\\n        - \u793e\u7fa4\u6d3b\u52d5\u66dd\u5149\uff1a\u5728\u6bcf\u6b21\u793e\u7fa4\u6d3b\u52d5\u7684\u958b\u5834\u548c\u4e2d\u5834\u4f11\u606f\u6642\uff0c\u6211\u5011\u5c07\u5f15\u4eba\u6ce8\u76ee\u5730\u5c55\u793a\u60a8\u7684\u4f01\u696d logo\uff0c\u540c\u6642\u9686\u91cd\u4ecb\u7d39\u8d0a\u52a9\u5546\u540d\u3002\\n        - \u7db2\u7ad9\u9732\u51fa\uff1a\u60a8\u7684\u4f01\u696d logo \u5c07\u5728\u6211\u5011\u7684[\u5b98\u65b9\u7db2\u7ad9][6]\u3001[CNCF Chapter][5] \u548c [Infra Labs][7] \u7db2\u7ad9\u4e0a\u5f97\u5230\u7a81\u986f\uff0c\u5c07\u60a8\u7684\u4f01\u696d\u8cc7\u8a0a\u50b3\u905e\u7d66\u793e\u7fa4\u6210\u54e1\u3002\\n        - \u806f\u5408\u5ba3\u50b3\uff1aCNTUG \u793e\u7fa4\u7db2\u7ad9\u548c\u570b\u969b CNCF \u884c\u92b7\u7ba1\u9053\u5ba3\u50b3\u3002\\n\\n## \u8d0a\u52a9\u4f7f\u7528\u8aaa\u660e\\n\\n1. \u5834\u5730\u79df\u501f\u8cbb\u7528\\n2. \u5834\u5730\u98df\u7269\u98f2\u6599\u63a1\u8cfc\u8cbb\u7528\\n3. \u5834\u5730\u8a2d\u5099\u79df\u501f\u8cbb\u7528\\n4. \u65e5\u5e38\u793e\u7fa4\u7d93\u71df\u5fc5\u8981\u8cbb\u7528\\n\\n## \u95dc\u65bc Cloud Native Taiwan User Group\\n\\n- [Cloud Native Taiwan User Group (CNTUG)][5] \u73fe\u70ba CNCF \u5b98\u65b9\u8a8d\u8b49\u7684\u5728\u5730\u793e\u7fa4\uff0c\u53ef\u4ee5\u4f9d\u64da CNCF \u898f\u7bc4\u5728\u5730\u9032\u884c\u5408\u4f5c\u6216\u6d3b\u52d5\\n- Cloud Native Taiwan User Group \u73fe\u884c\u65bc [Facebook \u793e\u5718][2]\uff0c\u5171\u8a08\u6709 6,600 \u4f4d\u6210\u54e1\u3002\\n- \u4f9d\u64da\u4e3b\u984c\u4e0d\u540c\uff0c\u5e73\u5747\u6709 3,000 \u4f4d\u6210\u54e1\u6709\u6301\u7e8c\u89c0\u5bdf\u53c3\u8207\u793e\u7fa4\u6d3b\u52d5\u3002\\n\\n## \u5e38\u898b\u554f\u7b54\\n\\n- Q1: \u53ef\u958b\u7acb\u767c\u7968\u55ce?\\n  - A1: \u53ef\u4ee5\uff0cCloud Native Taiwan User Group \u59d4\u7531[\u8ca1\u5718\u6cd5\u4eba\u958b\u653e\u6587\u5316\u57fa\u91d1\u6703 (OCF)][3] \u5354\u52a9\u793e\u7fa4\u52df\u6b3e\uff0c\u6240\u6709\u52df\u5f97\u6b3e\u9805\u7686\u5c08\u6b3e\u5c08\u7528\u4f86\u652f\u6301\u793e\u7fa4\u805a\u6703\u7d93\u8cbb\u3002\\n\\n- Q2: \u4ec0\u9ebc\u662f OCF?\\n  - A2: OCF \u70ba Open Culture Foundation\uff08\u8ca1\u5718\u6cd5\u4eba\u958b\u653e\u6587\u5316\u57fa\u91d1\u6703\uff09\u7684\u7c21\u7a31\uff0c\u662f\u500b\u5354\u52a9\u793e\u7fa4\u7372\u5f97\u884c\u653f\u548c\u8ca1\u52d9\u670d\u52d9\u7684\u975e\u71df\u5229\u7d44\u7e54\u3002Cloud Native Taiwan User Group \u7531\u300c\u8ca1\u5718\u6cd5\u4eba\u958b\u653e\u6587\u5316\u57fa\u91d1\u6703\uff08OCF\uff09\u300d\u5354\u529b\u958b\u7acb Cloud Native Taiwan User Group \u8d0a\u52a9\u6536\u64da\u3001\u96fb\u5b50\u767c\u7968\u3001\u7c3d\u8a02\u8d0a\u52a9\u5408\u7d04\u6216\u5176\u4ed6\u6cd5\u5f8b\u6587\u4ef6\u3002\\n\\n[1]: https://tag-env-sustainability.cncf.io/cloud-native-sustainability-week/\\n[2]: https://www.facebook.com/groups/cloudnative.tw\\n[3]: https://ocf.tw/p/community\\n[4]: https://events.linuxfoundation.org/wp-content/uploads/2023/08/sponsor-cncf-2023-081723.pdf\\n[5]: https://community.cncf.io/cloud-native-taiwan-user-group/\\n[6]: https://cloudnative.tw/\\n[7]: https://docs.cloudnative.tw/\\n[8]: https://www.meetup.com/gsf-london/\\n[9]: https://tag-env-sustainability.cncf.io/cloud-native-sustainability-week/#event-goals\\n[10]: https://docs.google.com/document/d/1s28lqqc3IBAMw4T7n13zfyAuUEEc3I4_xhPxCLA4_dk/edit#heading=h.pi10rftj9g4c"}]}')}}]);